import React, {useState, useEffect} from 'react';
import axios from 'axios';


const Home = () => {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3001/products')
            .then(result => {
                setProducts(result.data)
            })
    },[products])
    return (
        <div>
            <table>
                <thead>
                <tr>
                    <th>#</th>
                    <th>Tên sản phẩm</th>
                    <th>Giá</th>
                    <th>Tồn kho</th>
                </tr>
                </thead>
                <tbody>
                {products.map((product, index) => (
                    <tr key={product.id}>
                        <td>{index + 1}</td>
                        <td>{product.name}</td>
                        <td>{product.price}</td>
                        <td>{product.stock}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default Home;
