import React, {useState,useEffect} from 'react';
import {useParams} from 'react-router-dom';
import {useNavigate} from 'react-router-dom'
import axios from 'axios';
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";

const Edit = () => {
    const {productId}=useParams();
    const navigate = useNavigate();
    const [product,setProduct]=useState({});

    useEffect(()=>{
        axios.get('http://localhost:3001/products/'+productId)
            .then(result=>setProduct(result.data))
    },[productId])

    const handleEdit=(id)=>{
        axios.put('http://localhost:3001/products/'+id)
            .then(navigate('/'))
    }

    return (
        <div>
            <h1>Cập nhật sản phẩm</h1>
            <form action="">
                <b>Tên sản phẩm:</b> <br/><br/>
                <TextField value={product.name} name='name' placeholder='Tên sản phẩm' style={{width:'500px'}}/><br/><br/>
                <b>Giá sản phẩm:</b> <br/><br/>
                <TextField name='price' placeholder='Giá'/><br/><br/>
                <b>Tồn kho:</b> <br/><br/>
                <TextField name='stock' placeholder='Tồn kho'/>
                <br/><br/>
                <b>Mô tả sản phẩm:</b> <br/><br/>
                <TextField name='description' placeholder='Mô tả sản phẩm' style={{width:'800px'}}/><br/><br/>
                <Button variant='contained' onClick={()=>handleEdit(product.id)}>Thêm mới</Button>
                <Button variant="outlined" onClick={()=>navigate('/')}>Hủy</Button>
            </form>
        </div>
    );
};

export default Edit;
