import React, {useState,useEffect} from 'react';
import axios from 'axios';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';

const Add = () => {
    const [product,setProduct]=useState({});

    useEffect(()=>{
        axios.post('http://localhost:3001/products')
            .then()
    })

    return (
        <div>
            <h1>Thêm sản phẩm</h1>
            <form action="">
                <TextField name='name' label='Tên sản phẩm' style={{width:'500px'}}/><br/>
                <TextField name='price' label='Giá'/> <br/>
                <TextField name='stock' label='Tồn kho'/><br/>
                <TextField name='description' label='Mô tả sản phẩm' style={{width:'700px'}}/><br/>
                <Button variant='contained'>Thêm mới</Button>
                <Button variant="outlined">Hủy</Button>
            </form>
        </div>
    );
};

export default Add;
