import React, {useState} from 'react';
import axios from 'axios';


const Home = () => {
    const [products,setProducts]=useState()
    axios.get('http://localhost:3001/products')
        .then((result)=>{setProducts(result.data)})
    return (
        <div>
            <table>
                <tr>
                    <th>#</th>
                    <th>Tên sản phẩm</th>
                    <th>Giá</th>
                    <th>Tồn kho</th>
                </tr>
                {products.map((product,index)=>(
                    <tr>
                        <td>{index+1}</td>
                        <td>{product.name}</td>
                        <td>{product.price}</td>
                        <td>{product.stock}</td>
                    </tr>
                ))}
            </table>
        </div>
    );
};

export default Home;
